targets.expr:
(
  sum by (job) (
    rate(
      tenant_requests_total{path!="/metrics", status!~"[4|5].."}[$__rate_interval]
    )
  )
  /
  sum by (job) (
    rate(
      tenant_requests_total{path!="/metrics"}[$__rate_interval]
    )
  )
)

sum by (path) (
    rate(
        tenant_requests_total{path!="/metrics",status!~"[4|5].."}[$__rate_interval]
    )
)
/
sum by (path) (
    rate(
        tenant_requests_total{path!="/metrics"}[$__rate_interval]
    )
)

sum by (tenant) (
    rate(
        tenant_requests_total{path!="/metrics",status!~"[4|5].."}[$__rate_interval]
    )
)
/
sum by (tenant) (
    rate(
        tenant_requests_total{path!="/metrics"}[$__rate_interval]
    )
)

